return items.map(item => {
  const d = item.json;

  // Parse histogram if it's a string
  let histogram = {};
  if (d.popularTimesHistogram && typeof d.popularTimesHistogram === "string") {
    try {
      histogram = JSON.parse(d.popularTimesHistogram);
    } catch (e) {
      histogram = {};
    }
  } else if (typeof d.popularTimesHistogram === "object" && d.popularTimesHistogram !== null) {
    histogram = d.popularTimesHistogram;
  }

  // Find daily peak hours
  const dailyPeaks = Object.entries(histogram || {}).map(([day, hours]) => {
    if (!Array.isArray(hours) || hours.length === 0) return null;
    const peak = hours.reduce(
      (max, h) => h.occupancyPercent > max.occupancyPercent ? h : max,
      {hour: null, occupancyPercent: -1}
    );
    return peak.hour !== null ? `${day} peak: ${peak.hour}h (${peak.occupancyPercent}%)` : null;
  });

  // Extract review tags with counts preserved
  let reviewTagList = [];
  if (typeof d.reviewTags === "string") {
    reviewTagList = d.reviewTags
      .split(",")
      .map(t => t.trim())
      .filter(Boolean);
  } else if (Array.isArray(d.reviewTags)) {
    reviewTagList = d.reviewTags.map(t => t.title || t).filter(Boolean);
  }

  const tags = [
    ...(Array.isArray(d.reviewsTags) ? d.reviewsTags.map(t => t.title || t) : []),
    ...reviewTagList,
    ...(Array.isArray(d.categories) ? d.categories : []),
    ...(Array.isArray(d.attributes) ? d.attributes : []),

    // Live busyness
    d.popularTimesLiveText || null,
    d.popularTimesLivePercent ? `Live now: ${d.popularTimesLivePercent}%` : null,

    // Daily peak summaries
    ...dailyPeaks
  ]
    .filter(Boolean)
    .map(t => String(t).trim())
    .filter((v, i, a) => a.indexOf(v) === i); // dedupe

  return {
    json: {
      title: d.title || null,
      city: d.city || null,
      state: d.state || null,
      searchString: d.searchString || null,
      tags: tags.length ? tags : null
    }
  };
});

