const windowDays = 100; // configurable window
const now = new Date();
const cutoff = new Date(now.getTime() - windowDays * 24 * 60 * 60 * 1000);

const groups = {};

for (const item of items) {
  const d = item.json;

  if (!groups[d.title]) {
    groups[d.title] = {
      title: d.title,
      searchString: d.searchString,
      totalReviews: 0,
      recentReviews: 0,
    };
  }

  // Check if review has publishedAtDate
  if (d.publishedAtDate) {
    const reviewDate = new Date(d.publishedAtDate);
    if (reviewDate >= cutoff) {
      groups[d.title].recentReviews++;
    }
  }

  groups[d.title].totalReviews = d.reviewsCount || 0;
}

// Convert groups to array with RV
return Object.values(groups).map(g => {
  const rv = g.recentReviews > 0 
    ? (g.recentReviews / windowDays) * 30
    : 0;

  return {
    json: {
      title: g.title,
      searchString: g.searchString,
      reviewsCount: g.totalReviews,
      recentReviews: g.recentReviews,
      RV: rv.toFixed(2),
    }
  };
});

// n8n Code node: calculate average rating (AR) and variance (VAR)
// Needs: item.json.oneStar, twoStar, threeStar, fourStar, fiveStar, reviewsCount

const results = [];

for (const item of items) {
  const d = item.json;

  const counts = {
    1: d.oneStar || 0,
    2: d.twoStar || 0,
    3: d.threeStar || 0,
    4: d.fourStar || 0,
    5: d.fiveStar || 0,
  };

  const total = Object.values(counts).reduce((a,b) => a+b, 0);
  if (total === 0) {
    results.push({ json: { ...d, avgRating: null, ratingVariance: null }});
    continue;
  }

  // Average rating
  const sum = Object.entries(counts).reduce((a,[star,count]) => a + (parseInt(star) * count), 0);
  const mean = sum / total;

  // Variance
  const sqDiffSum = Object.entries(counts).reduce((a,[star,count]) => {
    const diff = parseInt(star) - mean;
    return a + count * diff * diff;
  }, 0);
  const variance = sqDiffSum / total;

  results.push({
    json: {
      ...d,
      avgRating: mean,
      ratingVariance: variance,
      reviewsCount: total
    }
  });
}

return results;

// n8n Code node: Calculate INC at provider (title) level and rank
// Needs input with fields: title, avgRating, ratingVariance, reviewsCount, website, phone, street, city, state, country, url, address, emails

const results = [];

// Step 1: Compute INC score per provider
for (const item of items) {
  const d = item.json;

  const rating = d.avgRating ?? null;
  const variance = d.ratingVariance ?? null;
  const count = d.reviewsCount ?? 0;

  // Binary INC flag: entrenched incumbents if rating high + variance low + review base strong
  let INC = 0;
  if (rating !== null && variance !== null) {
    if (rating >= 4.6 && variance < 0.15 && count > 50) {
      INC = 1;
    }
  }

  results.push({
    title: d.title || "unknown",
    avgRating: rating,
    ratingVariance: variance,
    reviewsCount: count,
    INC,
    website: d.website || null,
    phone: d.phone || null,
    street: d.street || null,
    city: d.city || null,
    state: d.state || null,
    country: d.country || null,
    url: d.url || null,
    address: d.address || null,
    emails: d.emails || []
  });
}

// Step 2: Sort by INC score (descending for top, ascending for bottom)
// To make top/bottom more meaningful, sort primarily by INC, then by reviewsCount
const sorted = results.sort((a, b) => {
  if (b.INC !== a.INC) return b.INC - a.INC;
  return (b.reviewsCount || 0) - (a.reviewsCount || 0);
});

// Step 3: Take top 10 and bottom 10
const top10 = sorted.slice(0, 10);
const bottom10 = sorted.slice(-10);

// Step 4: Return both lists
return [
  { json: { ranking: "Top 10 incumbents", providers: top10 } },
  { json: { ranking: "Bottom 10 incumbents", providers: bottom10 } }
];

// n8n Code node: group businesses per searchString with AR, VAR, and reviewsCount

const grouped = {};

for (const item of items) {
  const d = item.json;
  const key = d.searchString || "unknown";

  if (!grouped[key]) {
    grouped[key] = {
      searchString: key,
      businesses: []
    };
  }

  grouped[key].businesses.push({
    title: d.title,
    category: d.category || null,
    reviewsCount: d.reviewsCount,
    avgRating: d.avgRating,
    ratingVariance: d.ratingVariance,
    website: d.website || null,
    phone: d.phone || null,
    street: d.street || null,
    city: d.city || null,
    state: d.state || null,
    country: d.country || null,
    url: d.url || null,
    address: d.address || null,
    emails: d.emails || []
  });
}

// Return grouped array
return Object.values(grouped).map(g => ({ json: g }));

// n8n Code node: Calculate INC per business (title level)
// Input needs: title, avgRating, ratingVariance, reviewsCount (+ optional metadata)

return items.map(item => {
  const d = item.json;

  const rating = d.avgRating ?? null;
  const variance = d.ratingVariance ?? null;
  const count = d.reviewsCount ?? 0;

  // Rule: a business is "incumbent" if it has
  // high rating (>= 4.6), low variance (< 0.15), and strong review base (> 50)
  let INC = 0;
  if (rating !== null && variance !== null) {
    if (rating >= 4.6 && variance < 0.15 && count > 50) {
      INC = 1;
    }
  }

  return {
    json: {
      title: d.title || "unknown",
      category: d.category || null,
      avgRating: rating,
      ratingVariance: variance,
      reviewsCount: count,
      INC,
      // keep metadata
      website: d.website || null,
      phone: d.phone || null,
      street: d.street || null,
      city: d.city || null,
      state: d.state || null,
      country: d.country || null,
      url: d.url || null,
      address: d.address || null,
      emails: d.emails || []
    }
  };
});

const grouped = {};

for (const item of items) {
  const d = item.json;
  const key = d.searchString || "unknown";

  if (!grouped[key]) {
    grouped[key] = {
      searchString: key,
      businesses: []
    };
  }

  grouped[key].businesses.push({
    title: d.title,
    category: d.category || null,   // if you have category from previous node
    reviewsCount: d.reviewsCount,
    recentReviews: d.recentReviews,
    RV: d.RV
  });
}

// Return as array of searchStrings with businesses array
return Object.values(grouped).map(g => ({ json: g }));

// n8n Code node: Aggregate Review Velocity (RV) per search string

const results = [];

for (const item of items) {
  const d = item.json;
  const key = d.searchString || "unknown";

  let RVs = [];

  if (Array.isArray(d.businesses)) {
    for (const b of d.businesses) {
      if (b && b.RV !== null && b.RV !== undefined) {
        RVs.push(Number(b.RV));  // make sure it's numeric
      }
    }
  }

  const RV = RVs.length > 0 
    ? RVs.reduce((a, b) => a + b, 0) / RVs.length 
    : null;

  results.push({
    json: {
      cell_id: results.length + 1,
      keyword: key,
      RV
    }
  });
}

return results;

// n8n Code node: Provider Density
// Calculates how many providers exist per searchString

const groups = {};

for (const item of items) {
  const d = item.json;
  const key = d.searchString || "unknown";

  if (!groups[key]) {
    groups[key] = { 
      searchString: key,
      providers: 0,
      titles: [] 
    };
  }

  groups[key].providers += 1;
  groups[key].titles.push(d.title || "Unnamed");
}

// Return flattened results
return Object.values(groups).map(group => ({
  json: group
}));

// n8n Code node: Provider Density (PD) per search string

const results = [];

for (const item of items) {
  const d = item.json;
  const key = d.searchString || "unknown";

  // PD is just the count of providers
  const PD = d.providers || (Array.isArray(d.titles) ? d.titles.length : 0);

  results.push({
    json: {
      cell_id: results.length + 1,
      keyword: key,
      PD
    }
  });
}

return results;

// n8n Code node: INC rollup per searchString
const groups = {};

for (const item of items) {
  const d = item.json;
  const key = d.searchString || "unknown";

  if (!groups[key]) {
    groups[key] = { searchString: key, incumbents: 0, total: 0 };
  }

  // count total providers
  groups[key].total++;

  // count incumbents
  if (d.avgRating >= 4.6 && d.ratingVariance < 0.15 && d.reviewsCount > 50) {
    groups[key].incumbents++;
  }
}

// build % incumbents (INC)
return Object.values(groups).map(g => {
  const INC = g.total > 0 ? g.incumbents / g.total : 0;
  return {
    json: {
      searchString: g.searchString,
      totalProviders: g.total,
      incumbents: g.incumbents,
      INC: INC.toFixed(3) // 0â€“1 score
    }
  };
});

// n8n Code node: Aggregate AR and VAR per searchString (from businesses[])

const groups = {};

for (const item of items) {
  const d = item.json;
  const key = d.searchString || "unknown";

  if (!groups[key]) {
    groups[key] = {
      searchString: key,
      totalAR: 0,
      countAR: 0,
      totalVAR: 0,
      countVAR: 0,
    };
  }

  // Loop through businesses[]
  if (Array.isArray(d.businesses)) {
    for (const b of d.businesses) {
      if (b.avgRating !== undefined && b.avgRating !== null) {
        groups[key].totalAR += b.avgRating;
        groups[key].countAR++;
      }
      if (b.ratingVariance !== undefined && b.ratingVariance !== null) {
        groups[key].totalVAR += b.ratingVariance;
        groups[key].countVAR++;
      }
    }
  }
}

// Convert to array with averages
return Object.values(groups).map((g, i) => {
  const AR = g.countAR ? g.totalAR / g.countAR : null;
  const VAR = g.countVAR ? g.totalVAR / g.countVAR : null;

  return {
    json: {
      cell_id: i + 1,
      keyword: g.searchString,
      AR,
      VAR,
    }
  };
});

// Group tags/text/categories/peopleAlsoSearch by keyword or searchString
const grouped = {};

$input.all().forEach(item => {
  const d = item.json;
  const kw = d.keyword || d.searchString || "unknown";

  if (!grouped[kw]) {
    grouped[kw] = {
      tags: [],
      texts: [],
      categories: [],
      pas: []
    };
  }

  // Tags
  if (d.tags) {
    grouped[kw].tags.push(...(Array.isArray(d.tags) ? d.tags : [d.tags]));
  }

  // Review texts
  if (d.text) {
    grouped[kw].texts.push(d.text);
  }

  // Categories (use categoryName if available)
  if (d.categoryName) {
    grouped[kw].categories.push(d.categoryName);
  }

  // âœ… People Also Search
  if (Array.isArray(d.peopleAlsoSearch)) {
    d.peopleAlsoSearch.forEach(p => {
      if (p.title) {
        grouped[kw].pas.push(p.title);
      } else if (typeof p === "string") {
        grouped[kw].pas.push(p);
      }
    });
  } else if (typeof d.peopleAlsoSearch === "string" && d.peopleAlsoSearch.trim()) {
    d.peopleAlsoSearch.split(",").forEach(str => {
      grouped[kw].pas.push(str.trim());
    });
  }
});

// Build output
return Object.keys(grouped).map(kw => {
  const g = grouped[kw];
  return {
    json: {
      keyword: kw,
      tags: [...new Set(g.tags)],              // deduplicate tags
      texts: g.texts.slice(0, 50),             // cap at 50 review texts
      categories: [...new Set(g.categories)],  // deduplicate categories
      peopleAlsoSearch: [...new Set(g.pas)]    // deduplicate PAS
    }
  };
});

// n8n Code node: Flatten AI output arrays into strings for Sheets
return items.map((item, i) => {
  const d = item.json || {};

  // Utility: flatten an array of objects to readable strings
  function flattenArray(arr, fields) {
    if (!Array.isArray(arr)) return "";
    return arr.map(obj => {
      if (typeof obj === "string") return obj;
      if (typeof obj === "object") {
        // Pick only some fields to concatenate
        return fields.map(f => obj[f] || "").filter(Boolean).join(" | ");
      }
      return String(obj);
    }).join(" ; "); // separate entries with semicolon
  }

  const output = d.output || {};

  return {
    json: {
      keyword: d.keyword || "unknown",
      most_common_niches: flattenArray(output.a_most_common_niches, ["niche", "count"]),
      missing_high_potential_niches: flattenArray(output.b_missing_high_potential_niches, ["niche", "rationale"]),
      unique_or_differentiated_niches: flattenArray(output.c_unique_or_differentiated_niches, ["niche", "example_business", "rationale"]),
    }
  };
});

// n8n Code node: Group into single businesses array
// Output: one item with businesses[ {title, socials, website, histogram} ]

const grouped = {};

$input.all().forEach(item => {
  const d = item.json;
  const title = d.title || "unknown";

  if (!grouped[title]) {
    grouped[title] = {
      title,
      website: d.website || null,
      domain: d.domain || null,
      socials: {
        instagrams: [],
        facebooks: [],
        linkedins: [],
        twitters: [],
        youtubes: [],
        tiktoks: [],
        pinterests: []
      },
      popularTimesHistogram: []
    };
  }

  // Collect socials
